import { FormControl, Validators } from '@angular/forms';
import { SearchfieldComponent } from './searchfield.component';
const meta = {
    title: 'MyLib/Searchfield',
    component: SearchfieldComponent,
    tags: ['autodocs'],
    argTypes: {
        valueChange: { action: 'valueChange' },
        iconClick: { action: 'iconClick' },
        size: { control: { type: 'select' }, options: ['sm', 'md', 'lg'] },
        color: { control: { type: 'select' }, options: ['primary', 'secondary', 'success', 'error'] },
        iconStart: { control: 'text' },
        iconEnd: { control: 'text' },
    },
};
export default meta;
// ==========================
// Helper สำหรับสร้าง FormControl
const withControl = (value, required = false) => {
    const validators = [Validators.maxLength(10)];
    if (required)
        validators.push(Validators.required);
    return new FormControl(value || '', { nonNullable: true, validators });
};
// ==========================
// Shared render function
const renderTemplate = (args) => {
    const control = withControl(args.label);
    const valueAction = args.valueChange;
    control.valueChanges.subscribe((val) => {
        console.groupCollapsed(`Field Update: ${args.label || 'Field'}`);
        console.table({
            value: val,
            valid: control.valid,
            invalid: control.invalid,
            touched: control.touched,
            dirty: control.dirty,
            pristine: control.pristine,
            errors: control.errors,
        });
        console.groupEnd();
        valueAction?.(val);
    });
    return {
        props: { ...args, control },
        template: `
      <app-searchfield
        [label]="label"
        [placeholder]="placeholder"
        [control]="control"
        [size]="size"
        [color]="color"
        [iconStart]="iconStart"
        [iconEnd]="iconEnd"
        (valueChange)="valueChange($event)"
        (iconClick)="iconClick($event)"
      ></app-searchfield>
    `,
    };
};
// ========================
// Stories Examples
export const WithIcons = {
    render: renderTemplate,
    args: {
        label: 'Search',
        placeholder: 'Search here',
        size: 'sm',
        color: 'primary',
        iconStart: 'search',
        iconEnd: 'mic',
    },
};
export const WithoutIcons = {
    render: renderTemplate,
    args: {
        label: 'Search',
        placeholder: 'Search here',
        size: 'sm',
        color: 'primary',
        iconStart: undefined,
        iconEnd: undefined,
    },
};
// ========================
// State Stories
export const StateError = {
    render: renderTemplate,
    args: {
        label: 'Error State',
        placeholder: 'Enter max 10 characters',
        size: 'md',
        color: 'error',
        iconStart: 'error',
        iconEnd: undefined,
    },
    play: async ({ canvasElement }) => {
        const input = canvasElement.querySelector('input');
        if (input) {
            // ใส่ค่ามากกว่า 10 ตัวอักษรเพื่อ trigger error
            input.value = 'abcdefghijk'; // 11 ตัว
            input.dispatchEvent(new Event('input'));
            input.dispatchEvent(new Event('blur'));
        }
    },
};
export const StateFilled = {
    render: renderTemplate,
    args: {
        label: 'Filled State',
        placeholder: 'Placeholder here',
        size: 'md',
        color: 'success',
        iconStart: 'check',
    },
    play: async ({ canvasElement }) => {
        const input = canvasElement.querySelector('input');
        if (input) {
            input.value = 'Hello world'; // 11 ตัว
            input.dispatchEvent(new Event('input'));
        }
    },
};
