import { FormControl, Validators } from '@angular/forms';
import { TextfieldComponent } from 'my-lib';
const meta = {
    title: 'MyLib/Textfield',
    component: TextfieldComponent,
    tags: ['autodocs'],
    argTypes: {
        valueChange: { action: 'valueChange' }, // Action panel
        size: {
            control: { type: 'select' },
            options: ['sm', 'md', 'lg'],
        },
        color: {
            control: { type: 'select' },
            options: ['primary', 'secondary', 'success', 'error'],
        },
    },
};
export default meta;
// helper: create FormControl
const withControl = (value, required = false) => {
    const validators = [Validators.maxLength(10)];
    if (required)
        validators.push(Validators.required);
    return new FormControl(value || '', { nonNullable: true, validators });
};
// shared renderer
const renderTemplate = (args) => {
    const control = withControl(args.value, args.color === 'error');
    // subscribe for logging
    control.valueChanges.subscribe((val) => {
        console.groupCollapsed(`Field Update: ${args.label || 'Field'}`);
        console.table({
            value: val,
            valid: control.valid,
            invalid: control.invalid,
            touched: control.touched,
            dirty: control.dirty,
            pristine: control.pristine,
            errors: control.errors,
        });
        console.groupEnd();
    });
    return {
        props: { ...args, control },
        template: `
      <app-textfield
        [label]="label"
        [placeholder]="placeholder"
        [size]="size"
        [color]="color"
        [helperText]="helperText"
        [disabled]="disabled"
        [onlyNumbers]="onlyNumbers"
        [control]="control"
        (valueChange)="valueChange($event)"
      >
        <span class="material-symbols-outlined">clear</span>
      </app-textfield>
    `,
    };
};
// ========================
// Size Stories
// ========================
export const SizeSmall = {
    render: renderTemplate,
    args: { label: 'Small Field', value: '', size: 'sm', color: 'primary', helperText: 'Max 10 chars' },
};
export const SizeMedium = {
    render: renderTemplate,
    args: { label: 'Medium Field', value: '', size: 'md', color: 'primary', helperText: 'Max 10 chars' },
};
export const SizeLarge = {
    render: renderTemplate,
    args: { label: 'Large Field', value: '', size: 'lg', color: 'primary', helperText: 'Max 10 chars' },
};
// ========================
// Color Stories
// ========================
export const ColorPrimary = {
    render: renderTemplate,
    args: { label: 'Primary Field', value: '', size: 'md', color: 'primary', helperText: 'Max 10 chars' },
};
export const ColorSecondary = {
    render: renderTemplate,
    args: { label: 'Secondary Field', value: '', size: 'md', color: 'secondary', helperText: 'Max 10 chars' },
};
export const ColorSuccess = {
    render: renderTemplate,
    args: { label: 'Success Field', value: '', size: 'md', color: 'success', helperText: 'Max 10 chars' },
};
export const ColorError = {
    render: renderTemplate,
    args: { label: 'Error Field', value: '', size: 'md', color: 'error', helperText: 'Max 10 chars' },
};
// ========================
// State Stories
// ========================
export const Default = {
    render: renderTemplate,
    args: { label: 'Default', value: '', size: 'md', color: 'primary', helperText: 'Max 10 chars' },
};
export const WithValue = {
    render: renderTemplate,
    args: { label: 'With Value', value: 'Hello', size: 'md', color: 'primary', helperText: 'Max 10 chars' },
};
export const Disabled = {
    render: renderTemplate,
    args: { label: 'Disabled', value: '', size: 'md', color: 'primary', disabled: true, helperText: 'Max 10 chars' },
};
