import { FormControl, Validators } from '@angular/forms';
import { TextfieldComponent } from 'my-lib';
const meta = {
    title: 'MyLib/Textfield',
    component: TextfieldComponent,
    tags: ['autodocs'],
};
export default meta;
// helper: create FormControl
const withControl = (value, required = false) => {
    const validators = required
        ? [Validators.required, Validators.maxLength(10)]
        : [Validators.maxLength(10)];
    return new FormControl(value || '', validators);
};
// shared renderer
const renderTemplate = (args) => {
    const control = withControl(args.value, args.color === 'error');
    control.valueChanges.subscribe(() => {
        console.groupCollapsed(`Field Update: ${args.label || 'Field'}`);
        console.table({
            value: control.value,
            valid: control.valid,
            invalid: control.invalid,
            touched: control.touched,
            dirty: control.dirty,
            pristine: control.pristine,
            errors: control.errors,
        });
        console.groupEnd();
    });
    return {
        props: { ...args, control, valueChange: args.valueChange },
        template: `
      <app-textfield
        [label]="label"
        [placeholder]="placeholder"
        [size]="size"
        [color]="color"
        [helperText]="helperText"
        [disabled]="disabled"
        [onlyNumbers]="onlyNumbers"
        [control]="control"
        (valueChange)="valueChange($event)"
      >
        <span class="material-symbols-outlined">clear</span>
      </app-textfield>
    `,
    };
};
// default args
const defaultArgs = {
    label: 'Username',
    placeholder: 'Enter text',
    value: '',
    size: 'sm', // 'sm' | 'md' | 'lg'
    color: 'primary', // 'primary' | 'secondary' | 'success' | 'error'
    helperText: 'Max 10 characters',
    disabled: false,
    onlyNumbers: false,
};
// ========================
// Size Stories
// ========================
export const Size_Small = { render: renderTemplate, args: { ...defaultArgs, size: 'sm' } };
Size_Small.storyName = 'Small';
export const Size_Medium = { render: renderTemplate, args: { ...defaultArgs, size: 'md' } };
Size_Medium.storyName = 'Medium';
export const Size_Large = { render: renderTemplate, args: { ...defaultArgs, size: 'lg' } };
Size_Large.storyName = 'Large';
// ========================
// Color Stories
// ========================
export const Color_Primary = { render: renderTemplate, args: { ...defaultArgs, color: 'primary' } };
Color_Primary.storyName = 'Primary';
export const Color_Secondary = { render: renderTemplate, args: { ...defaultArgs, color: 'secondary' } };
Color_Secondary.storyName = 'Secondary';
export const Color_Success = { render: renderTemplate, args: { ...defaultArgs, color: 'success' } };
Color_Success.storyName = 'Success';
export const Color_Error = { render: renderTemplate, args: { ...defaultArgs, color: 'error' } };
Color_Error.storyName = 'Error';
// ========================
// State Stories
// ========================
export const State_Default = { render: renderTemplate, args: { ...defaultArgs } };
State_Default.storyName = 'Default';
export const State_Disabled = { render: renderTemplate, args: { ...defaultArgs, disabled: true } };
State_Disabled.storyName = 'Disabled';
export const State_WithValue = { render: renderTemplate, args: { ...defaultArgs, value: 'Pre-filled text' } };
State_WithValue.storyName = 'With Value';
