import { FormControl, Validators } from '@angular/forms';
import { SearchfieldComponent } from './searchfield.component';
const meta = {
    title: 'MyLib/Searchfield',
    component: SearchfieldComponent,
    tags: ['autodocs'],
    argTypes: {
        valueChange: { action: 'valueChange' },
        iconClick: { action: 'iconClick' },
        size: { control: { type: 'select' }, options: ['sm', 'md', 'lg'] },
        color: { control: { type: 'select' }, options: ['primary', 'secondary', 'success', 'error', 'focus'] },
        iconStart: { control: 'text' },
        iconEnd: { control: 'text' },
    },
};
export default meta;
// ==========================
// Helper à¸ªà¸³à¸«à¸£à¸±à¸šà¸ªà¸£à¹‰à¸²à¸‡ FormControl
const withControl = (value, required = false) => {
    const validators = [Validators.maxLength(10)];
    if (required)
        validators.push(Validators.required);
    return new FormControl(value || '', { nonNullable: true, validators });
};
// ==========================
// Shared render function
const renderTemplate = (args) => {
    const control = withControl(args.label);
    const valueAction = args.valueChange;
    control.valueChanges.subscribe((val) => {
        console.groupCollapsed(`Field Update: ${args.label || 'Field'}`);
        console.table({
            value: val,
            valid: control.valid,
            invalid: control.invalid,
            touched: control.touched,
            dirty: control.dirty,
            pristine: control.pristine,
            errors: control.errors,
        });
        console.groupEnd();
        valueAction?.(val);
    });
    return {
        props: { ...args, control },
        template: `
      <app-searchfield
        [label]="label"
        [placeholder]="placeholder"
        [control]="control"
        [size]="size"
        [color]="color"
        [iconStart]="iconStart"
        [iconEnd]="iconEnd"
        (valueChange)="valueChange($event)"
        (iconClick)="iconClick($event)"
      ></app-searchfield>
    `,
    };
};
// ========================
// Stories: With / Without Icons
export const WithIcons = {
    render: renderTemplate,
    args: {
        label: 'Search',
        placeholder: 'Search here',
        size: 'sm',
        color: 'primary',
        iconStart: 'search',
        iconEnd: 'mic',
    },
};
export const WithoutIcons = {
    render: renderTemplate,
    args: {
        label: 'Search',
        placeholder: 'Search here',
        size: 'sm',
        color: 'primary',
        iconStart: undefined,
        iconEnd: undefined,
    },
};
// ========================
// Size Stories
export const SizeSmall = {
    render: renderTemplate,
    args: {
        label: 'Small Field',
        placeholder: 'Search here',
        size: 'sm',
        color: 'primary',
        iconStart: 'search',
        iconEnd: 'mic',
    },
};
export const SizeMedium = {
    render: renderTemplate,
    args: {
        label: 'Medium Field',
        placeholder: 'Search here',
        size: 'md',
        color: 'primary',
        iconStart: 'search',
        iconEnd: 'mic',
    },
};
export const SizeLarge = {
    render: renderTemplate,
    args: {
        label: 'Large Field',
        placeholder: 'Search here',
        size: 'lg',
        color: 'primary',
        iconStart: 'search',
        iconEnd: 'mic',
    },
};
// ========================
// Color Stories
export const ColorPrimary = {
    render: renderTemplate,
    args: {
        label: 'Primary',
        placeholder: 'Search here',
        size: 'md',
        color: 'primary',
        iconStart: 'search',
        iconEnd: 'mic',
    },
};
export const ColorSecondary = {
    render: renderTemplate,
    args: {
        label: 'Secondary',
        placeholder: 'Search here',
        size: 'md',
        color: 'secondary',
        iconStart: 'search',
        iconEnd: 'mic',
    },
};
export const ColorSuccess = {
    render: renderTemplate,
    args: {
        label: 'Success',
        placeholder: 'Search here',
        size: 'md',
        color: 'success',
        iconStart: 'check',
        iconEnd: 'mic',
    },
};
export const ColorError = {
    render: renderTemplate,
    args: {
        label: 'Error',
        placeholder: 'Search here',
        size: 'md',
        color: 'error',
        iconStart: 'error',
        iconEnd: 'mic',
    },
};
// ========================
// State Stories
// ========================
export const StateError = {
    render: renderTemplate,
    args: {
        label: 'Error State',
        placeholder: 'Enter at least 5 characters',
        size: 'md',
        color: 'error',
        iconStart: 'error',
        iconEnd: undefined,
    },
    play: async ({ canvasElement }) => {
        const input = canvasElement.querySelector('input');
        // à¹ƒà¸ªà¹ˆà¸„à¹ˆà¸²à¸œà¸´à¸” + mark as touched à¹€à¸žà¸·à¹ˆà¸­à¹ƒà¸«à¹‰à¹€à¸à¸´à¸” error
        if (input) {
            input.value = 'a';
            input.dispatchEvent(new Event('input'));
            input.dispatchEvent(new Event('blur')); // à¸—à¸³à¹ƒà¸«à¹‰ touched
        }
    },
};
export const StateFocus = {
    render: renderTemplate,
    args: {
        label: 'Focus State',
        placeholder: 'Typing...',
        size: 'md',
        color: 'focus',
        iconStart: 'search',
    },
    play: async ({ canvasElement }) => {
        const input = canvasElement.querySelector('input');
        if (input)
            input.focus();
    },
};
// ðŸš« DISABLED STATE
export const StateDisabled = {
    render: (args) => {
        const control = new FormControl({ value: 'Disabled', disabled: true });
        return {
            props: { ...args, control },
            template: `
        <app-searchfield
          [label]="label"
          [placeholder]="placeholder"
          [control]="control"
          [size]="size"
          [color]="color"
          [iconStart]="iconStart"
          [iconEnd]="iconEnd"
        ></app-searchfield>
      `,
        };
    },
    args: {
        label: 'Disabled',
        placeholder: 'Cannot type',
        size: 'md',
        color: 'primary',
        iconStart: 'lock',
        iconEnd: undefined,
    },
};
// ðŸŸ¦ FILLED STATE (à¸¡à¸µà¸„à¹ˆà¸²à¹€à¸£à¸´à¹ˆà¸¡à¸•à¹‰à¸™ à¹à¸¥à¸° valid)
export const StateFilled = {
    render: renderTemplate,
    args: {
        label: 'Filled State',
        placeholder: 'Placeholder here',
        size: 'md',
        color: 'success',
        iconStart: 'check',
    },
    play: async ({ canvasElement }) => {
        const input = canvasElement.querySelector('input');
        if (input) {
            input.value = 'Hello world';
            input.dispatchEvent(new Event('input'));
        }
    },
};
