import { FormControl, Validators } from '@angular/forms';
import { TextfieldComponent } from 'my-lib';
const meta = {
    title: 'MyLib/Textfield',
    component: TextfieldComponent,
    tags: ['autodocs'],
    argTypes: {
        valueChange: { action: 'valueChange' }, // Action panel จะแสดงค่า
        size: {
            control: { type: 'select' },
            options: ['sm', 'md', 'lg'],
        },
        color: {
            control: { type: 'select' },
            options: ['primary', 'secondary', 'success', 'error'],
        },
    },
};
export default meta;
// helper: create FormControl
const withControl = (value, required = false) => {
    const validators = [
        Validators.maxLength(10),
        required ? Validators.required : undefined
    ].filter(Boolean);
    return new FormControl(value || '', {
        validators,
        nonNullable: true, // Type-safe กับ Angular v16+
    });
};
// shared renderer
const renderTemplate = (args) => {
    const control = withControl(args.value, args.color === 'error');
    // subscribe for logging in console
    control.valueChanges.subscribe((val) => {
        console.groupCollapsed(`Field Update: ${args.label || 'Field'}`);
        console.table({
            value: val,
            valid: control.valid,
            invalid: control.invalid,
            touched: control.touched,
            dirty: control.dirty,
            pristine: control.pristine,
            errors: control.errors,
        });
        console.groupEnd();
    });
    return {
        props: {
            ...args,
            control, // bind FormControl
        },
        template: `
  <app-textfield
    [label]="label"
    [placeholder]="placeholder"
    [size]="size"
    [color]="color"
    [helperText]="helperText"
    [disabled]="disabled"
    [onlyNumbers]="onlyNumbers"
    [control]="control"
    (valueChange)="valueChange($event)"
  />
`,
    };
};
// ========================
// Stories
// ========================
export const Default = {
    render: renderTemplate,
    args: {
        label: 'Username',
        placeholder: 'Enter text',
        value: '',
        size: 'sm',
        color: 'primary',
        helperText: 'Max 10 characters',
        disabled: false,
        onlyNumbers: false,
        valueChange: () => { }, // จะถูก Storybook action override
    },
};
export const WithValue = {
    render: renderTemplate,
    args: {
        ...Default.args,
        value: 'Pre-filled text',
    },
};
export const ErrorState = {
    render: renderTemplate,
    args: {
        ...Default.args,
        value: '',
        color: 'error',
    },
};
